import React from 'react';
import TronLinkGuide from 'components/TronLinkGuide';
import TronWeb from 'tronweb';
import Utils from 'utils';
import Swal from 'sweetalert2';


import './App.scss';

const FOUNDATION_ADDRESS = 'TWiWt5SEDzaEqS6kE5gandWMNfxR2B5xzg';

////////////////////////////////////////////////////////////////////////////////////
const contractAddress = 'TBaKKVgnfCWLRkuVJ2J3rhji53zTvNzeW8';   /// Add your contract address here
////////////////////////////////////////////////////////////////////////////////////

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            buyValue:10,

            tronWeb: {
              installed: false,
              loggedIn: false
            },
        }
        this.changeSide = this.changeSide.bind(this)
        this.init = this.init.bind(this)
        this.updateBuyValue = this.updateBuyValue.bind(this)

        this.buyTron = this.buyTron.bind(this);
    }

    async componentDidMount() {

        this.setState({loading:true})
        await new Promise(resolve => {
            const tronWebState = {
                installed: !!window.tronWeb,
                loggedIn: window.tronWeb && window.tronWeb.ready
            };

            if(tronWebState.installed) {
                this.setState({
                    tronWeb:
                    tronWebState
                });

                return resolve();
            }

            let tries = 0;

            const timer = setInterval(() => {
                if(tries >= 10) {
                    const TRONGRID_API = 'https://api.trongrid.io';

                    window.tronWeb = new TronWeb(
                        TRONGRID_API,
                        TRONGRID_API,
                        TRONGRID_API
                    );

                    this.setState({
                        tronWeb: {
                            installed: false,
                            loggedIn: false
                        }
                    });

                    clearInterval(timer);
                    return resolve();
                }

                tronWebState.installed = !!window.tronWeb;
                tronWebState.loggedIn = window.tronWeb && window.tronWeb.ready;

                if(!tronWebState.installed)
                    return tries++;

                this.setState({
                    tronWeb: tronWebState
                });

                resolve();
            }, 100);
        });

        if(!this.state.tronWeb.loggedIn) {
            // Set default address (foundation address) used for contract calls
            // Directly overwrites the address object as TronLink disabled the
            // function call
            window.tronWeb.defaultAddress = {
                hex: window.tronWeb.address.toHex(FOUNDATION_ADDRESS),
                base58: FOUNDATION_ADDRESS
            };

            window.tronWeb.on('addressChanged', () => {
                if(this.state.tronWeb.loggedIn)
                    return;

                this.setState({
                    tronWeb: {
                        installed: true,
                        loggedIn: true
                    }
                });
            });
        }

        await Utils.setTronWeb(window.tronWeb, contractAddress);


    }

    Update()
    {
        this.getBalance2();
        document.getElementById("getBal").innerHTML = "2te";
    }


    async getBalance()
    {
        var bank,tronWeb = 0;
        tronWeb = window.TronWeb;
        bank = await TronWeb.contract().at(contractAddress);
        //document.getElementById("getBal").innerHTML = "2te" + Utils.contract.testDisplay();
        document.getElementById("checkBalance").innerHTML = TronWeb.trx.getBalance();
        const bal = tronWeb.trx.testDisplay();
        console.log(bal);
    }

    async getBalance2(callback)
    {
        TronWeb.trx.getBalance(contractAddress).then(result => {
            callback(TronWeb.toDecimal(result));
        }).catch((err) => {
            console.log(err)
        })
        document.getElementById("getBal").innerHTML = "bleh: " + TronWeb.trx.getBalance(contractAddress);
    }

    async changeSide()
    {
        Utils.contract.buyTron2(this.state.buyValue).send({
            shouldPollResponse: true,
            callValue: 0
        }).then(res => Swal({
            title: 'Purchase Sucessful!',
            type: 'success'
        })).catch(err => Swal({
            title: 'Purchase Failed.',
            type: 'error'
        }));
    }

    async roll() {
        this.changeSide();
    };

    nextPlayer() {
    }

    init()
    {
        //var idleTron = new TronWeb.trx.contract(DiceGame.json, contractAddress)
        document.getElementById("dice-game").style.display = "block";
        //document.getElementById("roll-button").style.display = "block";
    }


    startEventListener(){

    }

    async updateBuyValue (evt)
    {
        if (evt.target.value < 10)
            evt.target.value = 10
        await this.setState({
            buyValue: evt.target.value
        });
    }

    async buyTron()
    {
        Utils.contract.buyTron2(this.state.buyValue).send({
                    shouldPollResponse: true,
                    callValue: 0
                }).then(res => Swal({
                    title: 'Post Created',
                    type: 'success'
                })).catch(err => Swal({
                    title: 'Post Failed',
                    type: 'error'
        }));
    }

    render() {
        if(!this.state.tronWeb.installed)
            return <TronLinkGuide />;

        if(!this.state.tronWeb.loggedIn)
            return <TronLinkGuide installed />;

        return (

            <div className='rw'>

                <div className="gameHeader">
                    <div className="headerTitle">
                        Idle Tron
                    </div>
                </div>
                <div className='text-center'>
                    <hr/>
                        <br></br>
                        <br></br>
                        <br></br>
                    <span value={this.state.buyValue}> MicroTron: </span>
                    <span id="getBal"></span>

                    <div className="div-bet">
                        Buy MicroTron:
                        <br></br>
                        <input style={{ width:"100px" }} value={this.state.buyValue} onChange={this.updateBuyValue}/>
                        <br></br>
                        <br></br>
                        <button className="buyMicroTronButton" onClick={(event) => {event.preventDefault(); this.getBalance();}}>
                            Buy Tron!
                        </button>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
